import app from "../../../index";
import chaiHttp from "chai-http";
import chai, { expect } from "chai";

chai.use(chaiHttp);
const router = () => chai.request(app);

describe("Blog Test Cases", () => {

    let createdBlogId = "";

    it("Should be able to add new Blog", (done) => {
      router()
        .post("/api/blog/add")
        .send({
          title: "Blog Title",
          description: "Blog description",
          image: "https://media.istockphoto.com/id/537331500/photo/programming-code-abstract-technology-background-of-software-deve.jpg?s=612x612&w=0&k=20&c=jlYes8ZfnCmD0lLn-vKvzQoKXrWaEcVypHnB5MuO-g8=",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", true);
          expect(response.body).to.have.property("message");
          createdBlogId = response.body.id;
          done(error);
        });
    });

    it("Should be able to get all blogs", (done) => {
      router()
        .get("/api/blog/blogs")
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", true);
          expect(response.body).to.have.property("message");
          expect(response.body.message).to.be.an("array");
          done(error);
        });
    });
    
    it("Should be able to delete created blog", (done) => {
        router()
          .delete("/api/blog/delete")
          .send({
           id: createdBlogId
          })
          .end((error, response) => {
            expect(response.body).to.be.a("object");
            expect(response.body).to.have.property("status", true);
            expect(response.body).to.have.property("message");
            done(error);
          });
      });

    
  });

import express from 'express';
import userRoutes from "./usersRoutes";
import suggestionRoutes from './suggestionRoutes';
import blogRoutes from './blogRoutes';

const router = express.Router();

router.use('/users', userRoutes);
router.use('/suggestion', suggestionRoutes)
router.use('/blog', blogRoutes)

export default router;

import jwt from 'jsonwebtoken'
import { encryptPassword, comparePassword } from './passwordUtils';

const generateToken = (userId) => {
    const token = jwt.sign({userId}, "SECRET_KEY", {expiresIn: '1d'});
    return token;
}



export {
    generateToken,
    encryptPassword,
    comparePassword,
}

import bcrypt from 'bcrypt';

const encryptPassword = async (password: string) =>{
    return await bcrypt.hash(password, 10);
}

const comparePassword = async (password: string, hashedPassword: string) =>{
    return await bcrypt.compare(password, hashedPassword);
}

export {
    encryptPassword,
    comparePassword
}

import { Router } from 'express'
import { addNewSuggestion, deleteSuggestion, getSuggestions } from '../modules/suggestions/controller/suggestionControllers';

const suggestionRoutes = Router();

suggestionRoutes.post("/add", addNewSuggestion)
suggestionRoutes.get("/suggestions", getSuggestions)
suggestionRoutes.delete("/delete", deleteSuggestion)

export default suggestionRoutes;

import { Router } from 'express'
import { addNewBlog, deleteBlog, getBlogs, getOneBlog } from '../modules/blog/controller/blogControllers';

const blogRoutes = Router();

blogRoutes.post("/add", addNewBlog)
blogRoutes.get("/blogs", getBlogs)
blogRoutes.get("/blog/:id", getOneBlog)
blogRoutes.delete("/delete", deleteBlog)

export default blogRoutes;

import app from "../../../index";
import chaiHttp from "chai-http";
import chai, { expect } from "chai";

chai.use(chaiHttp);
const router = () => chai.request(app);

describe("Suggestions Test Cases", () => {

    let createdSuggestionId = "";

    it("Should be able to add new suggestion", (done) => {
      router()
        .post("/api/suggestion/add")
        .send({
          name: "TestingSuggestion",
          email: "testuser@gmail.com",
          subject: "TestingSuggestionSubject",
          message: "This is actual message of the suggestion",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", true);
          expect(response.body).to.have.property("message");
          createdSuggestionId = response.body.id;
          done(error);
        });
    });

    it("Should be able to get all suggestions", (done) => {
      router()
        .get("/api/suggestion/suggestions")
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", true);
          expect(response.body).to.have.property("message");
          expect(response.body.message).to.be.an("array");
          done(error);
        });
    });
    
    it("Should be able to delete created suggestion", (done) => {
        router()
          .delete("/api/suggestion/delete")
          .send({
           id: createdSuggestionId
          })
          .end((error, response) => {
            expect(response.body).to.be.a("object");
            expect(response.body).to.have.property("status", true);
            expect(response.body).to.have.property("message");
            done(error);
          });
      });

    
  });

import app from "../../../index";
import chaiHttp from "chai-http";
import chai, { expect } from "chai";

chai.use(chaiHttp);
const router = () => chai.request(app);

describe("User Test Cases", () => {

    let token="";

    it("Should be able register new user", (done) => {
      router()
        .post("/api/users/register")
        .send({
          name: "TestingUser",
          email: "testuser@gmail.com",
          password: "PasswordForUser",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status");
          expect(response.body).to.have.property("message");
          expect(response.body.status).to.equal(true);
          done(error);
        });
    });
  
    it("Should not add same user twice", (done) => {
      router()
        .post("/api/users/register")
        .send({
          name: "TestingUser",
          email: "testuser@gmail.com",
          password: "PasswordForUser",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status");
          expect(response.body).to.have.property("message");
          expect(response.body.status).to.equal(false);
          expect(response.body.message).to.equal("User already exist.");
          done(error);
        });
    });

    it("Should be able to login registered user", (done) => {
      router()
        .post("/api/users/login")
        .send({
          email: "testuser@gmail.com",
          password: "PasswordForUser",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status");
          expect(response.body).to.have.property("message");
          expect(response.body.message).to.be.a("object");
          expect(response.body.message).to.have.property("token");
          expect(response.body.status).to.equal(true);
          token=response.body.message.token;
          done(error);
        });
    });

    it("Should be able to update registered user", (done) => {
      router()
        .patch("/api/users/update")
        .set("Authorization", `Bearer ${token}`)
        .send({
          name: "TestingUser-Updated",
          email: "testuser@gmail.com",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", true);
          expect(response.body).to.have.property("message");
          done(error);
        });
    });

    it("Should be able to delete registered user", (done) => {
      router()
        .delete("/api/users/delete")
        .set("Authorization", `Bearer ${token}`)
        .send({
          email: "testuser@gmail.com",
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", true);
          expect(response.body).to.have.property("message", "Deleted.");
          done(error);
        });
    });
  
    it("User should not be able to login with invalid credentials", (done) => {
      router()
        .post("/api/users/login")
        .send({
          email: "fake@gmail.com",
          password: "fakePassword"
        })
        .end((error, response) => {
          expect(response.body).to.be.a("object");
          expect(response.body).to.have.property("status", false);
          expect(response.body).to.have.property("message", "Invalid credentials");
          done(error);
        });
    });
  
  });
